package com.urdost.Adapter;import android.content.Context;import android.view.LayoutInflater;import android.view.View;import android.view.ViewGroup;import android.widget.TextView;import androidx.recyclerview.widget.RecyclerView;import com.urdost.R;import com.urdost.model.response.responsePayoutReport.LstPayout;import java.util.List;import butterknife.BindView;import butterknife.ButterKnife;public class AdapterPayoutReport extends RecyclerView.Adapter<AdapterPayoutReport.ViewHolder> {    private List<LstPayout> models;    private Context context;    public AdapterPayoutReport(List<LstPayout> models, Context context) {        this.models = models;        this.context = context;    }    @Override    public ViewHolder onCreateViewHolder(ViewGroup viewGroup, int i) {        View view = LayoutInflater.from(viewGroup.getContext()).inflate(R.layout.adapter_payout_report, viewGroup, false);        return new ViewHolder(view);    }    public void updatelist(List<LstPayout> models) {        this.models = models;        notifyDataSetChanged();    }    @Override    public void onBindViewHolder(ViewHolder viewHolder, int i) {        viewHolder.tvLoginId.setText(models.get(i).getEncryptLoginID());        viewHolder.tvName.setText(models.get(i).getDisplayName());        viewHolder.tvPayoutNumber.setText(models.get(i).getPayoutNo());        viewHolder.tvDirectIncome.setText(models.get(i).getDirectIncome());        viewHolder.tvDate.setText(models.get(i).getClosingDate());        viewHolder.tvMatchingBonus.setText(models.get(i).getLeadershipBonus());        ;        viewHolder.tvTdsAmount.setText(models.get(i).getTDSAmount());        viewHolder.tvNetAmount.setText(models.get(i).getNetAmount());    }    @Override    public int getItemCount() {        return models.size();    }    public class ViewHolder extends RecyclerView.ViewHolder {        @BindView(R.id.tv_loginId)        TextView tvLoginId;        @BindView(R.id.tv_name)        TextView tvName;        @BindView(R.id.tv_payout_number)        TextView tvPayoutNumber;        @BindView(R.id.tv_direct_income)        TextView tvDirectIncome;        @BindView(R.id.tv_date)        TextView tvDate;        @BindView(R.id.tv_matching_bonus)        TextView tvMatchingBonus;        @BindView(R.id.tv_Admin_charge)        TextView tvAdminCharge;        @BindView(R.id.tv_tds_amount)        TextView tvTdsAmount;        @BindView(R.id.tv_action)        TextView tvAction;        @BindView(R.id.tv_net_amount)        TextView tvNetAmount;        public ViewHolder(View view) {            super(view);            ButterKnife.bind(this, view);        }    }}